import React from 'react';
import './Card.css';

const Card = ({ id, title, tag, available, priority,name }) => {
  const statusClass = available ? 'cardContainer available' : 'cardContainer unavailable';

  // Define a symbol mapping based on status
  const prioritySymbols = {
    0: "﹍",
    1: '━',
    2: '═',
    3:  "≡",
    4: "⚠️"
  };
  const getInitials = (name) => {
    if (name) {
      const initials = name.slice(0, 2).toUpperCase();
      return initials;
    }
    return '';
  };

  return (
    <div className={`cardContainer flex-gap-10 ${statusClass}`} style={{ gap: '5px' }}>
      <div className="cardHeading flex-sb">
        <span style={{ textTransform: "uppercase" }} className='color-grey'>{id}</span>
        <div className="imageContainer relative" style={{ width: "30px", height: "30px" }}>
        <div className="noUserImage" style={{ backgroundColor: 'red' }}>
            {getInitials(name)}
          </div>
          <div className="showStatus"></div>
          </div>
      </div>
      <div className="cardTitle" style={{ fontWeight: 200 }} >
        <p>{title}</p>
      </div>
      <div className="cardTags">
        <div className="tags color-grey">{prioritySymbols[priority]}</div>
        {
          tag?.map((elem, index) => {
            return <div key={index} className="tags color-grey"> <span>•</span> {elem}</div>
          })
        }
      </div>
    </div>
  );
}

export default Card;
