import React from 'react';
import './Card.css';

const Card = ({ id, title, tag, available, priority, name, status, groupValue }) => {
  const statusClass = available ? 'cardContainer available' : 'cardContainer unavailable';

  const prioritySymbols = {
    0: '…',
    1: '▂',
    2: '▅',
    3: '▉',
    4: '⚠️'
  };

  const getInitials = (name) => {
    if (name) {
      const initials = name.slice(0, 2).toUpperCase();
      return initials;
    }
    return '';
  };

  const shouldDisplayStatusIcon = () => {
    return groupValue !== 'status'; // Hide status icon when the grouping is based on status
  };

  const getStatusIcon = (status) => {
    return shouldDisplayStatusIcon() ? getStatusSymbol(status) : '';
  };

  const getStatusSymbol = (status) => {
    switch (status) {
      case "In progress":
        return '◐';
      case "Backlog":
        return '⦿';
      case "Todo":
        return '〇';
      default:
        return '❔';
    }
  };

  // Generate a random color based on the user's name
  const randomColor = getRandomColor(name);

  function getRandomColor(name) {
    const seed = name
      .split('')
      .reduce((acc, char) => acc + char.charCodeAt(0), 0);

    const hue = (seed % 360).toString();

    return `hsl(${hue}, 70%, 70%)`;
  }

  return (
    <div className={`cardContainer flex-gap-10 ${statusClass}`} style={{ gap: '5px' }}>
      <div className="cardHeading flex-sb">
        <span style={{ textTransform: 'uppercase' }} className="color-grey">
          {id}
        </span>
        <div className="imageContainer relative" style={{ width: '30px', height: '30px' }}>
          <div className="noUserImage" style={{ backgroundColor: randomColor }}>
            {getInitials(name)}
          </div>
          {shouldDisplayStatusIcon() && <div className="showStatus">{getStatusIcon(status)}</div>}
        </div>
      </div>

      <div className="cardTitle">
        <p>{title}</p>
      </div>
      <div className="cardTags">
        <div className="tags color-grey">{prioritySymbols[priority]}</div>
        {tag?.map((elem, index) => (
          <div key={index} className="tags color-grey">
            <span>•</span> {elem}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Card;
