import React from 'react';
import { useSelector } from 'react-redux';
import './DashView.css';
import Card from '../Card/Card';
import { AiOutlinePlus } from "react-icons/ai";

const getInitials = (name) => {
  if (name) {
    const initials = name.slice(0, 2).toUpperCase();
    return initials;
  }
  return '';
};

const getStatusSymbol = (status) => {
  switch (status) {
    case "In progress":
      return 'ðŸš§';
    case "Backlog":
      return 'âœ…';
    case "Todo":
      return 'â³';
    default:
      return '';
  }
};

const randomColor = (name) => {
  if (name) {
    const seed = name
      .split('')
      .reduce((acc, char) => acc + char.charCodeAt(0), 0);
    const hue = (seed % 360).toString();
    return `hsl(${hue}, 70%, 70%)`;
  }
  // Return a default color or handle it as per your requirements when 'name' is undefined.
  return 'hsl(0, 0%, 70%)'; // Default gray color
};

const DashView = () => {
  const { selectedData, user } = useSelector((state) => state.SelectDataReducer);
  const allUser = useSelector((state) => state.DataReducer.allUser);

  return (
     selectedData && (
       <div className="dashContainer" style={{ justifyContent: 'space-evenly' }}>
         {selectedData.map((elem, index) => (
           <div key={index} className="dashCardContainer">
             <div className="dashCardHeading flex-sb">
               <div className="leftView">
                 {user ? (
                  <div className="imageContainer relative" style={{ width: '15px', height: '15px', display: 'inline-block' }}>
                     <div className="noUserImage" style={{ backgroundColor: randomColor(allUser.find((user) => user.id === elem.userId)?.name ?? 'unknown') }}>
                       {getInitials(allUser.find((user) => user.id === elem.userId)?.name ?? 'unknown')}
                     </div>
                  </div>
                 ) : (
                  <AiOutlinePlus size={20} color="#509afb" />
                 )}
               </div>
             </div>
             <div className="dashCardBody">
               {elem.tasks.map((task, ind) => (
                 <Card key={ind} id={task.id} title={task.title} tag={task.tag} status={task.status} priority={task.priority} available={allUser.find((user) => user.id === elem.userId)?.available} user={allUser.find((user) => user.id === elem.userId)?.name ?? 'unknown'} />
               ))}
             </div>
           </div>
         ))}
       </div>
     )
  );
 };

 export default DashView;
